import React, {useEffect, useState} from "react";
import axios from "axios";
const { Configuration, OpenAIApi } = require("openai");
const configuration = new Configuration({apiKey: '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',});
const openai = new OpenAIApi(configuration);

function Chat() {
    const [inputValue, setInputValue] = useState('')
    const [outputValue, setOutputValue] = useState('')
    const [history , setHistory]=useState([])
    const handleInputChange =  (event) => {
        setInputValue(event.target.value)
    }

    useEffect(()=>{
        async function callApi() {
            const completion = await openai.createChatCompletion({
            model: "gpt-3.5-turbo",
                messages:history
            });
            const response = completion.data.choices[0].message.content
            setOutputValue(response)
            //console.log(history)

        }

        callApi()
    },[history])

    async function submit(e) {
        e.preventDefault()
        setHistory((prevHistory) => [...prevHistory, { role: 'user', content: inputValue }]);




    }
    return(
        <form className='bg-primary vh-100 p-5' onSubmit={submit}>
            <div className=' bg-dark w-50 container rounded p-3 h-100'>
                <h2 className='text-primary'>Chat GPT</h2>

                <div className='d-flex justify-content-around'>
                    <input className='input-group-text col-9' value={inputValue} onChange={handleInputChange}  />
                    <button className='btn btn-primary col-2'>Translate</button>
                </div>

                <div className='m-4'>
                    <h5 className='text-primary ' >Answer</h5>
                    <p className='text-white'>{outputValue}</p>

                </div>

            </div>
        </form>
    )
}


export default Chat
