import React, {useState} from "react";
import axios from "axios";
import Message from "../Component/Message";

function Chat() {
    const [inputValue , setInputValue]  = useState('')
    const [msg , setMsg]= useState([])

    const handleInputChange =  (event) => {
        setInputValue(event.target.value)
    };

    async function submit(e) {
        e.preventDefault()

        if(inputValue!=''){
            setMsg((prev)=>[...prev,{type : "msg" , value : inputValue}])
            setInputValue('')


            axios.post("http://192.168.1.5:3000/open",{"question":inputValue}).then((res)=>{
                setMsg((prev)=>[...prev,{type : "rep" , value : res.data}])
                console.log(`Q : ${inputValue}    | A : ${res.data}`)
            })
                .then((res)=>{
                console.log(res)
            })
                .catch((e)=>alert(e.value))
        }
    }

    const style = {backgroundColor:"#26a5fd", backgroundImage: "linear-gradient(135deg, #26a5fd 6%, #80D0C7 90%)"}
    return(
        <div style={style} className='vh-100  p-3' >
            <div  className='  bg-dark w-50 container rounded p-3 h-100 '>
                <h2 className='text-primary'>Chat Bot</h2>

                <div className='bg-dark border border-secondary rounded overflow-auto modal-dialog-scrollable container pt-2  h-75'>
                    <small className='text-light-emphasis d-flex justify-content-center pb-3'>Ask what ever you want !</small>

                    {
                        msg.map((value, index)=>{
                            return <Message key={index} type ={value.type} data={value.value}/>
                        })
                    }


                </div>
                <form  onSubmit={submit}>
                    <h5 className='text-primary ' >Answer</h5>

                    <div className="form-outline d-flex">
                        <textarea className="form-control" value={inputValue}  id="textAreaExample1" rows="4"></textarea>
                            <button className='btn btn-dark col-2' onClick={submit}>send</button>

                    </div>

                </form>



            </div>
        </div>
    )
}


export default Chat
