import React, {useState} from "react";
import axios from "axios";
import Message from "../Component/Message";

function Chat() {
    const [inputValue , setInputValue]  = useState('')
    const [outputValue, setOutputValue] = useState('')
    const [msg , setMsg]= useState([])

    const handleInputChange =  (event) => {
        setInputValue(event.target.value)
    };


    async function submit() {
        setMsg((prev)=>[...prev,{type : "msg" , value : inputValue}])

        // axios.post("http://192.168.1.5:3000/open",{"question":inputValue}).then((res)=>{
        //     setOutputValue(res.data)
        //     setMsg((prev)=>[...prev,{type : "msg" , value : inputValue}])
        //     setInputValue('')
        //     console.log(`Q : ${inputValue}    | A : ${res.data}`)
        // }).then((res)=>{
        //     console.log(res)
        // })
    }
    function test() {
        console.log(msg)
    }


    const style = {backgroundColor:"#26a5fd", backgroundImage: "linear-gradient(135deg, #26a5fd 6%, #80D0C7 90%)"}
    return(
        <div style={style} className='vh-100  p-4' >
            <div  className='  bg-dark w-50 container rounded p-3 h-100 '>
                <h2 className='text-primary'>Chat GPT</h2>

                <div className='bg-dark border border-secondary rounded overflow-auto modal-dialog-scrollable container pt-2  h-75'>
                    <small className='text-light-emphasis d-flex justify-content-center pb-3'>Ask what ever you want !</small>
                    <Message type = 'msg' data='Hello' />
                    <Message type = 'rep' data='Hi there ! askldsalkdj askdnalsd askdnalsdn sdkfjs;dfkjsd;lfk;l aslkdaklmasd' />

                    {
                        msg.map((value, index)=>{
                            <h2>{value}</h2>
                        })
                    }


                </div>
                <div className=''>
                    <h5 className='text-primary ' >Answer</h5>
                    <div  className='d-flex justify-content-around '>
                        <input className='input-group-text col-9' value={inputValue} onChange={handleInputChange}  />
                        <button className='btn btn-primary col-2' onClick={submit}>Translate</button>
                        <button onClick={test} >Test</button>

                    </div>
                </div>



            </div>
        </div>
    )
}


export default Chat
