import React, {useEffect} from "react";
import {useState} from "react";

function App() {

    const [inputValue, setInputValue] = useState('');
    const [outputValue, setOutputValue] = useState('');

    const handleInputChange =  (event) => {
        setInputValue(event.target.value);
    };


    async function submit(e) {
        e.preventDefault()
        const response = await fetch('/api/data');
        //const data = await response.json();
        setOutputValue(response);
    }
    return(
        <form className='bg-primary vh-100 p-5' onSubmit={submit}>
            <div className=' bg-dark w-50 container rounded p-3 h-100'>
                <h2 className='text-primary'>Chat GPT</h2>

                <div className='d-flex justify-content-around'>
                    <input className='input-group-text col-9' value={inputValue} onChange={handleInputChange}  />
                    <button className='btn btn-primary col-2'>Ask</button>
                </div>

                <div className='m-4'>
                    <h5 className='text-primary ' >Answer</h5>
                    <p className='text-white'>{outputValue}</p>

                </div>



            </div>
        </form>
    )
}
export default App
